<span class="line"><span class="token variable">$sirene</span><span class="token operator">-&gt;</span><span class="token function">searchEstablishments</span><span class="token punctuation">(</span><span class="token variable">$parameters</span><span class="token punctuation">)</span><span class="token operator">-&gt;</span><span class="token function">firstPage</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
